import React, { useEffect, useRef } from 'react';
import './App.css';
import Intro from './Start';
import About from './About';
import Skills from './Skills';
import Projects from './Projects';
import 'aos/dist/aos.css'; // Import AOS styles
import AOS from 'aos'; // Import AOS library

function App() {
  const introRef = useRef(null);
  const aboutRef = useRef(null);
  const skillsRef = useRef(null);
  const projectsRef = useRef(null);

  useEffect(() => {
    AOS.init({
      duration: 1000, // Animation duration
      once: true, // Whether animation happens only once
    });
  }, []);

  return (
    <div>
      <div className="right-sidebar">
        <div className="sidebar-icon" onClick={() => introRef.current.scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-house"></i>
        </div>
        <div className="sidebar-icon" onClick={() => aboutRef.current.scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-address-card"></i>
        </div>
        <div className="sidebar-icon" onClick={() => skillsRef.current.scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-screwdriver-wrench"></i>
        </div>
        <div className="sidebar-icon" onClick={() => projectsRef.current.scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-diagram-project"></i>
        </div>
      </div>

      <section ref={introRef} data-aos="fade-up">
        <Intro />
      </section>
      <section ref={aboutRef} data-aos="fade-up">
        <About />
      </section>
      <section ref={skillsRef} data-aos="fade-up">
        <Skills />
      </section>
      <section ref={projectsRef} data-aos="fade-up">
        <Projects />
      </section>
    </div>
  );
}

export default App;
