// src/App.js
import React, { useState } from 'react';
import { CSSTransition, SwitchTransition } from 'react-transition-group';
import './App.css';
import Intro from './Intro';
import About from './About';
import Skills from './Skills';
import Projects from './Projects';

function App() {
  const [currentSection, setCurrentSection] = useState('intro');

  const renderSection = () => {
    switch (currentSection) {
      case 'intro':
        return <Intro />;
      case 'about':
        return <About />;
      case 'skills':
        return <Skills />;
      case 'projects':
        return <Projects />;
      default:
        return <Intro />;
    }
  };

  return (
    <div>
      <div className="right-sidebar">
        <div className="sidebar-icon" onClick={() => setCurrentSection('intro')}>
          <i className="fa-solid fa-house"></i>
        </div>
        <div className="sidebar-icon" onClick={() => setCurrentSection('about')}>
          <i className="fa-solid fa-address-card"></i>
        </div>
        <div className="sidebar-icon" onClick={() => setCurrentSection('skills')}>
          <i className="fa-solid fa-screwdriver-wrench"></i>
        </div>
        <div className="sidebar-icon" onClick={() => setCurrentSection('projects')}>
          <i className="fa-solid fa-diagram-project"></i>
        </div>
      </div>

      <SwitchTransition>
        <CSSTransition
          key={currentSection}
          timeout={300}
          classNames="fade"
        >
          <div>{renderSection()}</div>
        </CSSTransition>
      </SwitchTransition>
    </div>
  );
}

export default App;
