import React, { useEffect, useRef } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';
import Intro from './Start';
import About from './About';
import Skills from './Skills';
import Projects from './Projects';
import Footer from "./Footer";
import './App.css';

function App() {
  const sectionsRef = useRef([]);

  useEffect(() => {
    AOS.init({
      duration: 1000,  // Duration of the fade animation
      once: false,     // Allow repeated animations when scrolling up/down
      mirror: true,    // Animation happens when scrolling back up
    });

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('aos-animate');  // Fade in
            entry.target.classList.remove('fade-out');    // Ensure fade-out class is removed when visible
          } else {
            entry.target.classList.add('fade-out');      // Fade out
          }
        });
      },
      { threshold: 0.1 }  // Animation starts when 10% of the section is visible
    );

    const currentSections = sectionsRef.current; // Store a copy of the ref value

    currentSections.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => {
      currentSections.forEach((section) => {
        if (section) observer.unobserve(section);
      });
    };
  }, []);

  return (
    <div>
      <div className="right-sidebar">
        <div className="sidebar-icon" onClick={() => document.getElementById('intro').scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-house"></i>
        </div>
        <div className="sidebar-icon" onClick={() => document.getElementById('about').scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-address-card"></i>
        </div>
        <div className="sidebar-icon" onClick={() => document.getElementById('skills').scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-screwdriver-wrench"></i>
        </div>
        <div className="sidebar-icon" onClick={() => document.getElementById('projects').scrollIntoView({ behavior: 'smooth' })}>
          <i className="fa-solid fa-diagram-project"></i>
        </div>
      </div>

      <section
        id="intro"
        data-aos="fade-up"
        ref={(el) => (sectionsRef.current[0] = el)}
      >
        <Intro />
      </section>

      <section
        id="about"
        data-aos="fade-up"
        ref={(el) => (sectionsRef.current[1] = el)}
      >
        <About />
      </section>

      <section
        id="skills"
        data-aos="fade-up"
        ref={(el) => (sectionsRef.current[2] = el)}
      >
        <Skills />
      </section>

      <section
        id="projects"
        data-aos="fade-up"
        ref={(el) => (sectionsRef.current[3] = el)}
      >
        <Projects />
      </section>
      <Footer/>
    </div>
  );
}

export default App;
