import React, { useState, useRef } from 'react';
import Intro from './Intro';
import About from './About';
import Skills from './Skills';
import Projects from './Projects';
import { CSSTransition, SwitchTransition } from 'react-transition-group';
import './App.css';

function App() {
  const [currentSection, setCurrentSection] = useState('intro');

  // Refs for scrolling to sections
  const sectionRefs = {
    intro: useRef(null),
    about: useRef(null),
    skills: useRef(null),
    projects: useRef(null),
  };

  const scrollToSection = (section) => {
    setCurrentSection(section);
    const element = sectionRefs[section].current;
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const renderSection = () => {
    switch (currentSection) {
      case 'intro':
        return <Intro ref={sectionRefs.intro} />;
      case 'about':
        return <About ref={sectionRefs.about} />;
      case 'skills':
        return <Skills ref={sectionRefs.skills} />;
      case 'projects':
        return <Projects ref={sectionRefs.projects} />;
      default:
        return <Intro ref={sectionRefs.intro} />;
    }
  };

  return (
    <div>
      {/* Sidebar navigation for scrolling */}
      <div className="right-sidebar">
        <div className="sidebar-icon" onClick={() => scrollToSection('intro')}>
          <i className="fa-solid fa-house"></i>
        </div>
        <div className="sidebar-icon" onClick={() => scrollToSection('about')}>
          <i className="fa-solid fa-address-card"></i>
        </div>
        <div className="sidebar-icon" onClick={() => scrollToSection('skills')}>
          <i className="fa-solid fa-screwdriver-wrench"></i>
        </div>
        <div className="sidebar-icon" onClick={() => scrollToSection('projects')}>
          <i className="fa-solid fa-diagram-project"></i>
        </div>
      </div>

      {/* SwitchTransition component to handle smooth transitions between sections */}
      <SwitchTransition>
        <CSSTransition
          key={currentSection}
          timeout={300}
          classNames="fade"
        >
          <div>{renderSection()}</div>
        </CSSTransition>
      </SwitchTransition>
    </div>
  );
}

export default App;
